$(function(){$('.checkboxes-list .checkAll, .checkboxes-list .uncheckAll').click(function(a){a.preventDefault();var b=$(this).parents('.checkboxes-list').find('input[type="checkbox"]');if($(this).hasClass('checkAll'))b.prop('checked',true);else b.prop('checked',false)})});