function close_flashMessage(){$('#flashMessage').animate({opacity:'0'},'800',function(){$(this).slideUp('200')})}$(function(){$('.checkboxes-list .checkAll, .checkboxes-list .uncheckAll').click(function(a){a.preventDefault();var b=$(this).parents('.checkboxes-list').find('input[type="checkbox"]');if($(this).hasClass('checkAll'))b.prop('checked',true);else b.prop('checked',false)});$('.tip.tip-hidden .tip-title').click(function(){$(this).css('cursor','default');$(this).next('.tip-text').slideDown('slow')});$('#flashMessage .close').click(function(){close_flashMessage();return false});setTimeout(close_flashMessage,3500)});