(function($){$.fn.extend({slugify:function(d,e){d=typeof d!=='undefined'?d:'form #title';e=typeof e!=='undefined'?e:'form #slug';function getSlug(a){a=a.toLowerCase();var b="àáäâèéëêìíïîòóöôùúüûñç·/_,:;";var c="aaaaeeeeiiiioooouuuunc------";for(var i=0,l=b.length;i<l;i++)a=a.replace(new RegExp(b.charAt(i),'g'),c.charAt(i));return a.replace(/[^a-z0-9 -]/g,'').replace(/^\s+|\s+$/g,'').replace(/\s+/g,'-').replace(/-+/g,'-')}$(d).add(e).change(function(){if(!$(e).val().length&&$(d).val().length)$(e).val(getSlug($(d).val()));else $(e).val(getSlug($(e).val()))})}})})(jQuery);